	/**
	* Populate random grid
	* @para 	
	* @return 	array
	*/
	public function init()
	{
		$value = 0;
		return array_fill(0, $this->cols, array_fill(0, $this->rows, $value));
	}


	/**
	* Populate next generation grid
	* @para 	array $grid
	* @return 	array $next
	*/
	public function next($grid)
	{
		for ($i=0; $i < $this->cols; $i++)
		{ 
	        for ($j=0; $j < $this->rows; $j++)
	        { 
	            $state = $grid[$i][$j]; 

	            $neighbors = $this->neighbors($grid, $i, $j);

	            if ($state == 0 && $neighbors == 3)
	            {
	                $next[$i][$j] = 1;
	            } 
	            else if ($state == 1 && ($neighbors < 2 || $neighbors > 3))
	            {
	                $next[$i][$j] = 0;
	            } 
	            else
	            {
	                $next[$i][$j] = $state;
	            }
	        }
	    }
	    return $next;
	}

	 
	/**
	* Count alive/on neighbor cells
	* @para 	array $grid; int $x; int $y
	* @return 	int $cell
	*/
	public function neighbors($grid, $x, $y)
	{
	    $cell = 0;
	    for ($i=-1; $i < 2; $i++)
	    { 
	        for ($j=-1; $j < 2; $j++)
	        { 
	            $col = ($x + $i + $this->cols) % $this->cols;
	            $row = ($y + $j + $this->rows) % $this->rows;
	            $cell += $grid[$col][$row];
	        }
	    }
	    $cell -= $grid[$x][$y];
	    return $cell;
	}

	/**
	* Return game of life patterns
	* @para 	string $value
	* @return 	array  	
	*/
	public function patterns($value)
	{
		return $this->patterns[$value];
	}

